import React from 'react'
import { useState } from 'react';


export default function OpenAI() {
    const [txt, setTxt]=useState("");
    

  
  return (
    <>
    <div className='aimain'>

    <h1 className='title'>Hello! this is Marv here</h1>
    <input type="textarea" className='text1' placeholder='Ask Me Anything'onChange={(e)=>{setTxt(e.target.value)}} />
    <input type="Submit" className="button" placeholder='submit' onClick={()=>{setTxt("Marv will reply")}  }/>
    <input type="textarea" className="response" placeholder="Marv's response will appear here" value={txt} />
    <button className='button' onClick={()=>{setTxt("")}}>Clear Res</button>
    
    </div>
    </>
  );
  
}
